import React, { Component } from 'react';

export default class Timer extends Component {
    constructor(props){
        super(props)
        this.state = {
            endtime: this.props.endtime,
            count: Math.floor((new Date(this.props.endtime).getTime() - new Date().getTime())/(1000))
        }
    }

 render()   {
    const {count} = this.state
    const interval = new Date(count * 1000).toISOString().substr(11, 8)
    return (
         <div>
            <h1> Test timer: {interval} </h1>
         </div>
     )
 }

 componentDidMount(){
     this.myInterval = setInterval(()=> {
        this.setState(prevState => ({count: this.state.count - 1}), ()=>{
            if(this.state.count <1){
                clearInterval(this.myInterval)
            }
        })
     }, 1000)
 }

 componentWillUnmount(){
     clearInterval(this.myInterval)
 }
}
