import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios'

export default class CreateGroup extends Component {
    constructor(props) {
        super(props)

        this.onSubmit = this.onSubmit.bind(this);
        this.onChangeGroupname = this.onChangeGroupname.bind(this);
        this.onChangeUsers = this.onChangeUsers.bind(this);
        this.onChangeCount = this.onChangeCount.bind(this);

        this.state = {
            groupName: '',
            groupMembers: [''],
            groupCount: 0
        }    
    }

    componentDidMount(){
        axios.get(`http://localhost:5000/users`)
            .then(res => {
                this.setState(state => {
                    const groupMembers = state.groupMembers.concat(res.data.username);
                    return {
                        groupMembers,
                    }
                })            })
            .catch((error) => {
                console.log(error);
              })

    } 

    onChangeGroupname(e){
        this.setState({
            groupName: e.target.value
        })
    }

    onChangeCount(e){
        this.setState({
            groupCount: e.target.value
        })
    }  

    onChangeUsers(e){
        this.setState(state => {
            const groupMembers = state.groupMembers.concat(e.target.value);
            return {
                groupMembers,
            }
        })
    }

    onSubmit(e) {
        e.preventDefault();

        const group = {
            groupName: this.state.groupName,
            groupMembers: this.state.groupMembers,
            groupCount: this.state.groupCount
        }

        console.log(group)

        axios.post(`http://localhost:5000/group`, group)
            .then(res => console.log(res.data))
            .catch((error) => {
                console.log(error);
              })

        this.setState({
            groupName: '',
            groupMembers: [''],
            groupCount: 0
        })
    }

    render(){
        return (
            <div>
                Add Group
                <form onSubmit={this.onSubmit}>
                <label>Group name: </label>
                <input  type="text"
                    required
                    value={this.state.name}
                    onChange={this.onChangeGroupname}
                />
                <label>Members: </label>
                <select  ref="userInput"
                    required
                    value={this.state.groupCount}
                    onChange={this.onChangeUsers}>
                    {
                        this.state.groups.map(function(group) {
                            return <option 
                              key={group}
                              value={group}>{group}
                              </option>
                        })
                    }
                    </select>
                <input type="submit" value="Create Group"/>
                </form>

            </div>    
        )
        }
}
