import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios'

export default class CreateGroup extends Component {
    constructor(props) {
        super(props)

        this.onSubmit = this.onSubmit.bind(this);
        this.onChangeGroupname = this.onChangeGroupname.bind(this);
        this.onChangeUsers = this.onChangeUsers.bind(this);
        this.onChangeCount = this.onChangeCount.bind(this);

        this.state = {
            groupName: '',
            groupMembers: [],
            groupCount: 0,
            users: []
        }    
    }

    componentDidMount(){
        axios.get(`http://localhost:5000/users`)
            .then(res => {
                this.setState({users: res.data});
                console.log(this.state.users);

            })
            .catch((error) => {
                console.log(error);
              })

    } 

    onChangeGroupname(e){
        this.setState({
            groupName: e.target.value
        })
    }

    onChangeCount(e){
        this.setState({
            groupCount: e.target.value
        })
    }  

    onChangeUsers(e){
        let groupMembers = this.state.groupMembers.slice();
        this.setState({
            groupMembers: this.state.groupMembers.concat(e.target.value)
        }    
        )
    }

    onSubmit(e) {
        e.preventDefault();

        const group = {
            groupName: this.state.groupName,
            groupMembers: this.state.groupMembers,
            groupCount: this.state.groupCount
        }

        console.log(group)

        axios.post(`http://localhost:5000/groups/add`, group)
            .then(res => console.log(res.data))
            .catch((error) => {
                console.log(error);
              })

        this.setState({
            groupName: '',
            groupMembers: [],
            groupCount: 0
        })
        window.location = '/groups';
    }

    render(){
        return (
            <div>
                <h3> Add Group</h3>
                <form onSubmit={this.onSubmit}>          
                    <div className="form-group">
                        <label>Group name: </label>
                        <input  type="text" className="form-control" required value={this.state.name} onChange={this.onChangeGroupname} />
                    </div>
                    <div className="form-group">
                        <label>Members: </label>
                        <select  ref="userInput" className="form-control" required value={this.state.groupMembers} onChange={this.onChangeUsers}>
                            {
                                this.state.users.map((username)=>{
                                    return <option key={_id} value={username}>
                                            {username}
                                            </option>;
                                })
                            }
                        </select>
                    </div>
                    <div className="form-group">
                        <input type="submit" className="form-control" value="Add Group"/>
                    </div>
                    <div className="form-group">
                        <h3> Selected Members</h3>

                    </div>
                </form>
            </div>    
        )
        }
}
