        super(props)

        this.onSubmit = this.onSubmit.bind(this);

        this.state = {
            name: '',
            username: ''
        }    
    }

    onSubmit(e) {
        e.preventDefault();

        const user = {
            name: this.state.name,
            username: this.state.username
        }

        console.log(user)

        axios.post(`http://localhost:3000/users/add`, user)
            .then(res => console.log(res.data))
