import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios'

const Group = props => (
    <tr>
        <td>{props.group.groupname}</td>
        <td>{props.user.username}</td>
    </tr>
);

export default class ShowGroups extends Component {
    constructor(props) {
        super(props)
        this.setState({
            groupName: '',
            groupMembers: [''],
            groupCount: 0
        })  
    }

    componentDidMount(){
        axios.get(`http://localhost:5000/groups`)
            .then(res => {
                this.setState({groups: res.data});
            })
            .catch((error) => {
                console.log(error);
              })

    }

    groupList(){
        return this.state.users.map(currentgroup => {
             return <Group group={currentuser} key={currentuser._id}/>})
    }

    render() {
        return (
            <div>
                Logged Users

            <table>
            <thead>
                <tr>
                    <th>Group Name</th>
                    <th>Members</th>
                    <th>Number of Members</th>
                </tr>
            </thead>
                <tbody>
                { this.groupList() }
                </tbody>
            </table>
            </div>    
        );
    }
}
